import itertools

def hamming_numbers():
    factors = (2, 3, 5)
    heap = [(val, i, val) for i, val in enumerate(factors)]
    heapq.heapify(heap)

    yield 1
    seen = {1}

    for val in itertools.count(2):
        while heap[0][0] < val:
            _, i, val0 = heapq.heappop(heap)
            val1 = val0 * factors[i]
            if val1 not in seen:
                heapq.heappush(heap, (val1, i, val0))
                seen.add(val1)

        yield heap[0][0]

