def three_sum(array):
    array.sort()
    result = set()
    for i in range(len(array) - 2):
        if i > 0 and array[i] == array[i - 1]:
            continue
        for j in range(i + 1, len(array) - 1):
            if j > i + 1 and array[j] == array[j - 1]:
                continue
            target = -(array[i] + array[j])
            idx = bisect_left(array, target, j + 1)
