    gap = (gap * 10) // 13
    if gap < 1:
        return 1
    return gap

def comb_sort(data):
    gap = len(data)
    swapped = True
    while gap !=1 or swapped:
        gap = update_gap(gap)
        swapped = False
        for i in range(len(data) - gap):
            if data[i] > data[i + gap]:
                data[i], data[i + gap] = data[i + gap], data[i]
                swapped = True

