def cutRod(price, n):
    if n <= 0:
        return 0
    else:
        memo = [-1]*(n+1)
        memo[0] = 0 # base case: no rod left to cut

        for i in range (1, n+1):
            q = float('-inf')

            for j in range (i):
                q = max(q, price[j] + memo[i-j-1]) # Try all possible cuts and pick the one with maximum value

            memo[i] = q

        return memo[n]

